
CREATE TABLE sensors (
    id          serial PRIMARY KEY,
    sensor_id   integer NOT NULL,
    dt          date,
    value1      integer,
    value2      float,
    value3      float,
    value4      varchar(5)
);

CREATE TABLE events (
    id          serial PRIMARY KEY,
    sensor_id   integer,
    dt          timestamp,
    value_1     integer,
    value_2     float,
    value_3     float,
    heat_index  float,
    power       varchar(6)
);

